(function () {
  angular.module("ng-material-grid", []).
      directive("ngMaterialGrid", function () {
    return {
      restrict: 'EA',
      scope: {
        config: '=',
        data: '='
      },
      templateUrl: 'ng-material-grid.html',
      link: function (scope, element, attrs) {
        scope.theme = "md-" + (scope.config.theme || 'primary');

        scope.rows = [];
        _.each(scope.data, function (datum) {
          var rowData = [];
          _.each(scope.config.columnDefs, function (def) {
            rowData.push({
              val: _.get(datum, def.field),
              width: def.width,
              align: def.align
            });
          });
          scope.rows.push(rowData);
        });

      }
    }
  })
})();
angular.module("ng-material-grid").run(["$templateCache", function($templateCache) {$templateCache.put("ng-material-grid.html","<md-content flex=\"\" layout=\"column\" class=\"ng-material-grid\"><md-content class=\"ng-material-grid-header-wrapper\"><md-toolbar class=\"{{theme}} ng-material-grid-header\" flex=\"\"><div flex=\"\" layout=\"\"><span flex=\"{{def.width}}\" ng-repeat=\"def in config.columnDefs\" layout=\"\" class=\"layout-align-{{def.align}}-center ng-material-grid-cell\">{{def.headerText}}</span></div></md-toolbar></md-content><md-content flex=\"\" class=\"ng-material-grid-body\" layout=\"column\"><div ng-repeat-start=\"row in rows\" layout=\"\" class=\"ng-material-grid-row\"><span flex=\"{{cell.width}}\" ng-repeat=\"cell in row\" layout=\"\" class=\"layout-align-{{cell.align}}-center ng-material-grid-cell\">{{cell.val}}</span></div><md-divider ng-repeat-end=\"\"></md-divider></md-content><md-content class=\"ng-material-grid-footer\" ng-if=\"config.footer\"><md-toolbar class=\"{{theme}}\"></md-toolbar></md-content></md-content>");}]);